<template>
  <div>
    <input type="file" @change="uploadFileChange" />
    <div class="preview-box" v-if="previewUrl">
      <div class="preview-title">原图:</div>
      <canvas class="preview-img" ref="previewContent"></canvas>
    </div>
    <div class="preview-box" v-if="previewUrl">
      <div class="preview-title">滤镜:</div>
      <canvas class="preview-img" ref="filterContent"></canvas>
    </div>
    <form v-if="previewUrl">
      <label>
        <input name="filter" type="radio" v-model="filter" value="1" @change="filterRender" />去色
      </label>
      <label>
        <input name="filter" type="radio" v-model="filter" value="2" @change="filterRender" />反色
      </label>
      <label>
        <input name="filter" type="radio" v-model="filter" value="3" @change="filterRender" />单色(红)
      </label>
      <label>
        <input name="filter" type="radio" v-model="filter" value="4" @change="filterRender" />单色(绿)
      </label>
      <label>
        <input name="filter" type="radio" v-model="filter" value="5" @change="filterRender" />单色(蓝)
      </label>
    </form>
  </div>
</template>
<script>
export default {
  data() {
    return {
      previewUrl:
        "data:image/jpeg;base64,",
      filter: "1"
    };
  },
  mounted() {
    this.renderCanvas("previewContent");
    this.renderCanvas("filterContent");
  },
  methods: {
    async uploadFileChange(e) {
      this.previewUrl = await this.fileToBase64(e.target.files[0]);
      this.renderCanvas("previewContent");
      this.renderCanvas("filterContent");
      this.filterRender();
    },
    // 渲染图片
    async renderCanvas(ref) {
      await this.$nextTick();
      var img = new Image(); // 声明新的Image对象
      img.src = this.previewUrl;
      // 图片加载后
      img.onload = () => {
        var canvas = this.$refs[ref];
        var ctx = canvas.getContext("2d");
        // 根据image大小，指定canvas大小
        canvas.width = img.width;
        canvas.height = img.height;
        // 绘制图像
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
        if (ref === "filterContent") {
          this.filterRender();
        }
      };
    },
    async uploadFileChange(e) {
      this.previewUrl = await this.fileToBase64(e.target.files[0]);
      this.renderCanvas("previewContent");
      this.renderCanvas("filterContent");
    },
    filterRender() {
      const RenderMap = {
        "1": this.decoloring,
        "2": this.antiColor
      };
      RenderMap[this.filter]();
    },
    //去色滤镜
    decoloring() {
      let canvas = this.$refs["filterContent"];
      let ctx = canvas.getContext("2d");
      let imgData = ctx.getImageData(0, 0, canvas.width, canvas.height);
      for (let i = 0; i < imgData.data.length / 4; ++i) {
        let red = imgData.data[i * 4],
          green = imgData.data[i * 4 + 1],
          blue = imgData.data[i * 4 + 2];
        let gray = 0.3 * red + 0.59 * green + 0.11 * blue; // 计算gray
        // 刷新RGB，注意：
        // imgData.data[i * 4 + 3]存放的是alpha，不需要改动
        imgData.data[i * 4] = gray;
        imgData.data[i * 4 + 1] = gray;
        imgData.data[i * 4 + 2] = gray;
      }
      ctx.putImageData(imgData, 0, 0); // 重写图像数据
    },
    //反色滤镜
    antiColor() {
      let canvas = this.$refs["filterContent"];
      let ctx = canvas.getContext("2d");
      let imgData = ctx.getImageData(0, 0, canvas.width, canvas.height);
      for (let i = 0; i < imgData.data.length / 4; ++i) {
        let red = imgData.data[i * 4],
          green = imgData.data[i * 4 + 1],
          blue = imgData.data[i * 4 + 2];
        // 刷新RGB，注意：
        // imgData.data[i * 4 + 3]存放的是alpha，不需要改动
        imgData.data[i * 4] = 255 - imgData.data[i * 4];
        imgData.data[i * 4 + 1] = 255 - imgData.data[i * 4 + 1];
        imgData.data[i * 4 + 2] = 255 - imgData.data[i * 4 + 2];
      }
      ctx.putImageData(imgData, 0, 0); // 重写图像数据
    },
    fileToBase64(file) {
      let that = this,
        reader = new FileReader();
      reader.readAsDataURL(file);
      return new Promise((resolve, reject) => {
        reader.onload = e => {
          if (e.target.result) {
            resolve(e.target.result);
          } else {
            reject("error");
          }
        };
      });
    }
  }
};
</script>
<style lang="less" scoped>
.preview-img {
  display: block;
  width: 300px;
  height: 200px;
  background: #eee;
}
</style>